    Integer determineSantaFloor (String input) {
        Integer floor = 0
        input.toList().each { String c ->
            if (c == '(') {
                ++floor
            }
            if (c == ')') {
                --floor
            }
        }
        return floor
    }

    Integer determineFirstBasement (String input) {
        Integer counter = 0
        Integer floor = 0
        Integer output = null
        boolean found = false
        input.toList().each { String c ->
            ++counter
            if (c == '(') {
                ++floor
            }
            if (c == ')') {
                --floor
            }
            if (floor < 0 && !found) {
                output = counter
                found = true
            }
        }
        return output
    }
